import Vue from 'vue'
import VueRouter from 'vue-router'
import Home from '../views/Home.vue'
import Ingresar from '../views/Ingresar.vue'
import Ver from '../views/Ver.vue'
import Editar from '../views/Editar.vue'


Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home
  },
   {
    path: '/ingresar',
    name: 'Ingresar',
    component: Ingresar

  },
   {
    path: '/ver',
    name: 'Ver',
    component: Ver

  },
  {
    path: '/editar/:nombre/:descripcion/:cat/:id', 
    name: 'Editar',
    component: Editar 
  }


]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

export default router
